mapper.select:
SELECT ID, COMMENT_CONTENT, COMMENT_REGISTER_DATE, COMMENT_UPDATE_DATE, MEMBER_ID, MEMBER_NAME, POST_ID FROM ( SELECT ROWNUM R, RE.ID, COMMENT_CONTENT, COMMENT_REGISTER_DATE, COMMENT_UPDATE_DATE, RE.MEMBER_ID, MEMBER_NAME, POST_ID FROM ( SELECT RE.ID, COMMENT_CONTENT, COMMENT_REGISTER_DATE, COMMENT_UPDATE_DATE, RE.MEMBER_ID, MEMBER_NAME, POST_ID FROM TBL_MEMBER M JOIN TBL_COMMENT RE ON POST_ID = #{id} AND M.ID = RE.MEMBER_ID ORDER BY RE.ID DESC ) RE WHERE
ROWNUM <= #{pagination.page} * #{pagination.rowCount}
)
WHERE R > (#{pagination.page} - 1) * #{pagination.rowCount}

mapper.update:
UPDATE TBL_COMMENT
SET COMMENT_CONTENT = #{commentContent}, COMMENT_UPDATE_DATE = SYSDATE
WHERE ID = #{id}
